mapper.select:
select

from student
where 1=1

  and pk_domain = #{pkDomain}


  and istype = #{istype}


  and istype != 0


  and isvalid = #{isvalid}


  and phone = #{phone}


  and caption LIKE "%"#{caption}"%"


  and isminority = #{isminority}


  and grade = #{grade}


  and (dorm_room IS NULL OR dorm_room='')


  and (school_bus IS NULL OR  school_bus='')


  and pk_sys_user in(select pk_sys_user from sys_user where pk_department in(select pk_department from sys_role_department where pk_sys_role in(select pk_sys_role from sys_user_role where pk_sys_user=#{powerUser})) or pk_sys_user =#{powerUser})


  and pk_student in(select pk_student from classinfo_student where pk_classinfo =#{pkClassinfo} and isvalid = 1 )


  and pk_student in(select pk_student from student_interview_record where is_type =1 )


  and pk_student NOT in(select pk_student from student_interview_record where is_type =1 )


  and code =#{code}


order by creation_date DESC

select s.code,s.caption studentName from classinfo_student cs
left join student s on s.pk_student = cs.pk_student
where cs.pk_classinfo=#{pkClassInfo}

  and s.code=#{code}


  and s.caption=#{studentName}

order by s.creation_date asc

select pk_student studentId,code ,caption studentName,img from student where 1=1

  and pk_student in

    #{id}
