$(document).ready(function () {
    $.ajax({
        type: "GET",
        url: "/system/sysPower/query",
        data: {"pkSysMenu": ""},
        dataType: "json",
        success: function (data) {
            var htmlText = "";
            if (data.code == 0) {
                if (data.data == null) {
                    return;
                }
                var list = data.data;

                // console.log(list);
                var homeText = '<li><a href="/sigonindex"><i class="fa fa-home"></i> <span>首页</span></a></li>';
                for (var i = 0; i < list.length; i++) {
                    var sysMenu = list[i];
                    if (sysMenu.isdisplay != 1) {
                        continue;
                    }
                    var rootText = "";
                    var childText = "";
                    var actionClassText = '<li class="nav-parent">';
                    var classText = '<ul class="children">';
                    for (var j = 0; j < list.length; j++) {
                        var sysMenuChild = list[j];
                        var actionChildClassText = '<li>';
                        if (sysMenuChild.pkParent != sysMenu.pkSysMenu || sysMenuChild.isdisplay != 1) {
                            continue;
                        }
                        var currentUrl = window.location.pathname;
                        var currentMenuUrl = sysMenuChild.url;
                        if (sysMenuChild.url != '') {
                            currentMenuUrl = currentMenuUrl.replace("query", "");
                        }
                        if (currentMenuUrl != '' && currentUrl.indexOf(currentMenuUrl) != -1) {
                            actionChildClassText = '<li class="active">';
                            classText = '<ul class="children" style="display: block;">';
                            actionClassText = '<li class="nav-parent nav-active active">';
                        }
                        if (getCookie("locale") == "en_US") {
                            childText = childText + actionChildClassText + '<a href=' + sysMenuChild.url + '><i class="fa fa-caret-right"></i>' + sysMenuChild.captionEng + '</a></li>';
                        }
                        else {
                            childText = childText + actionChildClassText + '<a href=' + sysMenuChild.url + '><i class="fa fa-caret-right"></i>' + sysMenuChild.caption + '</a></li>';
                        }
                    }
                    if (childText != "") {
                        if (getCookie("locale") == "en_US") {
                            rootText = actionClassText + '<a href=""><i class="' + sysMenu.img + '"></i> <span>' + sysMenu.captionEng + '</span></a>' + classText;
                        }
                        else {
                            rootText = actionClassText + '<a href=""><i class="' + sysMenu.img + '"></i> <span>' + sysMenu.caption + '</span></a>' + classText;
                        }
                        rootText = rootText + childText + "</ul></li>";
                    }
                    if (rootText != "") {
                        htmlText = htmlText + rootText;
                    }
                }
                $("#sysMenuId").html(homeText + htmlText);
                if ($("#sysMenuId").text() == "") {

                }
                else {
                    //alert(JSON.stringify($("#sysMenuId").text()));
                }
            } else {
                alert(data.message)
            }
        },
        error: function () {

        }
    });
});
function GetTableColumn(tableName) {
    var tableColums = [];
    $.ajax({
            type: "GET",
            url: "/system/sysTableColumn/query",
            data: {"pkSysTableColumn": tableName},
            dataType: "json",
            async: false,
            success: function (data) {
                for (var i = 0; i < data.length; i++) {
                    var entity;
                    var title = data[i].sTitle;
                    if (getCookie("locale") == "en_US") {
                        title = data[i].sTitleEng
                    }
                    if (data[i].render != '' && data[i].render != 'null') {
                        entity = {"sTitle": title, "data": data[i].data, "render": window[data[i].render]};
                    }
                    else {
                        entity = {"sTitle": title, "data": data[i].data};
                    }
                    tableColums.push(entity);
                }
            },
            error: function () {

            }
        }
    );
    return tableColums;
}
function GetRequest() {
    var url = location.search; //获取url中含"?"符后的字串
    var theRequest = new Object();
    if (url.indexOf("?") != -1) {
        var str = url.substr(1);
        strs = str.split("&");
        for (var i = 0; i < strs.length; i++) {
            theRequest[strs[i].split("=")[0]] = unescape(strs[i].split("=")[1]);
        }
    }
    return theRequest;
}
function getTable(tableName){
    return $('#' + tableName).dataTable();
}
function init(columns, url, data,tableName) {
    var type = "GET";
    /*if (pagingTable != null) {
        pagingTable.fnClearTable(); //清空一下table
        pagingTable.fnDestroy(); //还原初始化了的datatable
    }*/
    if ($('#'+tableName).attr("init") == "true") {
        getTable(tableName).fnSettings().ajax.url = url;
        getTable(tableName).fnSettings().ajax.data = data;
        getTable(tableName).fnDraw();
    }
    else {
        $('#'+tableName).attr("init", true);
        $('#'+tableName).dataTable({
            "processing": true,
            "serverSide": true,
            retrieve: true,
            responsive: true,
            //destroy: true,
            "dom": 'T<"clear">lfrtip',
            "columnDefs": [{"orderable": false, "targets": 0}],
            "language": {
                "sProcessing": "数据加载中...",
                "lengthMenu": "每页 _MENU_ 条记录",
                "zeroRecords": "没有找到记录",
                "info": "第 _PAGE_ 页 ( 总共 _PAGES_ 页,总共 _TOTAL_ 条 )",
                "infoEmpty": "无记录",
                "infoFiltered": "(从 _MAX_ 条记录过滤)",
                "sInfoPostFix": "",
                "sSearch": "搜索:",
                "sUrl": "",
                "sEmptyTable": "表中数据为空",
                "sLoadingRecords": "载入中...",
                "sInfoThousands": ",",
                "Next": "下一页",
                "oPaginate": {
                    "sFirst": "首页",
                    "sPrevious": "上页",
                    "sNext": "下页",
                    "sLast": "末页"
                },
                "oAria": {
                    "sSortAscending": ": 以升序排列此列",
                    "sSortDescending": ": 以降序排列此列"
                }

            },
            "ajax": {
                "url": url,
                "type": type,
                "data": data
            },
            "columns": columns

        });
    }
}
