function timestampConvertDateYHMD(data) {
    var newDate = new Date();
    newDate.setTime(data);
    return newDate.format("yyyy-MM-dd hh:mm:ss");
}
function timestampConvertDateYYYY(data) {
    var newDate = new Date();
    newDate.setTime(data);
    return newDate.format("yyyy");
}
Date.prototype.format = function (format) {
    var date = {
        "M+": this.getMonth() + 1,
        "d+": this.getDate(),
        "h+": this.getHours(),
        "m+": this.getMinutes(),
        "s+": this.getSeconds(),
        "q+": Math.floor((this.getMonth() + 3) / 3),
        "S+": this.getMilliseconds()
    };
    if (/(y+)/i.test(format)) {
        format = format.replace(RegExp.$1, (this.getFullYear() + '').substr(4 - RegExp.$1.length));
    }
    for (var k in date) {
        if (new RegExp("(" + k + ")").test(format)) {
            format = format.replace(RegExp.$1, RegExp.$1.length == 1
                ? date[k] : ("00" + date[k]).substr(("" + date[k]).length));
        }
    }
    return format;
}

function timestampConvertDateAge(data) {
    // 获得今天的时间
    var date = new Date();
    var birthday = data;
    var startDate = new Date(birthday);
    var newDate = date.getTime() - startDate.getTime();
    // 向下取整  例如 10岁 20天 会计算成 10岁
    // 如果要向上取整 计算成11岁，把floor替换成 ceil
    var age = Math.ceil(newDate / 1000 / 60 / 60 / 24 / 365);
    if (isNaN(age)) {
        age = "";
    }
    return age;
}
//出生日期转化为年龄
